<?phpclass AdminLogin extends CI_Model{//Login Authentication	public function authenticate($admin_email,$admin_password)	{		//Run Query		$resultObject=$this->db->select('*')								->from('tb_admin')								->where("admin_email='".$admin_email."' and admin_password='".$admin_password."'")								->get();		//Get Result		//$result=$resultObject->result_array();		//Return value		if($resultObject->num_rows()==1)			return array('admin_id'=>$resultObject->row()->admin_id,'admin_upload_dir_id'=>$resultObject->row()->admin_upload_dir_id,'admin_image'=>$resultObject->row()->admin_image,'admin_name'=>$resultObject->row()->admin_name);		else			return FALSE;	}}